create table user
(
    user_id    int auto_increment
        primary key,
    name       varchar(100)  not null comment 'user actual name',
    password   varchar(100)  not null,
    username   varchar(100)  not null comment 'user number',
    permission int default 0 not null comment '0: student, 1: teacher, 2: admin',
    photo      mediumtext    null comment '用户图像Base64编码，未来可以考虑存blob',
    constraint user_pk
        unique (username)
);

